import React, { Component } from "react";
import '../css/Login.css'
import 'bootstrap/dist/css/bootstrap.min.css'
import axios from "axios";
import Cookies from "universal-cookie";

//const url = "http://localhost:5000/usuarios";

const cookies = new Cookies();

class PageRegistro extends Component {

  state = {
    form: {
      usu_nombres:'',
      usu_apellidos:'',
      usu_email:'',
      usu_clave:'',
      registroClave:''
    }
  }

  handleChange = async e => {  /// función para capturar os datos del usuario. Es en 2do plano debe ser asincrona
    await this.setState({   /// await regresa la ejecución de la función asincrona despues de terminar
      form: {
        ...this.state.form, /// esta linea sirve para conservar los datos que ya tenia el arreglo
        [e.target.name]:e.target.value  /// los nombres de los imputs deben ser iguales a los del arreglo
      }
    })
    console.log(this.state.form);  /// probar por consola lo que se guarda
  }

registrarUsuario = async() => {
    let usu_nombres=this.state.form.usu_nombres
    let usu_apellidos = this.state.form.usu_apellidos
    let usu_email = this.state.form.usu_email
    let usu_clave =this.state.form.usu_clave
    let registroClave = this.state.form.registroClave
    if (usu_nombres==='' || usu_clave ==='' || usu_email ==='' || usu_apellidos ===''){
        alert('Se requieren todos los datos')
        return "Datos vacios"
    }
    if (usu_clave !== registroClave){
        alert('Las contraseñas no coinciden')
        return "Contraseñas no coinciden"
    }

    axios.get(process.env.REACT_APP_USUARIOS + "/" + usu_email + "/" + pwd).then(response => {
        if(response.data ===''){
            alert('Este usuario ya existe')
            return "El usuario ya existe"
        }

        delete this.state.form.registroClave
        alert('Registrado con éxito')
        this.peticionPost();
    }).catch(error =>{
        console.log(error)
    })
}

peticionPost = () => {
    axios.post(process.env.REACT_APP_USUARIOS, this.state.form).then(response => {
        //console.log(response.data)
        this.iniciarSesion()
    }).catch(error => {
        console.log(error)
    })
this.iniciarSesion()
}

iniciarSesion = () => {
    axios.get(process.env.REACT_APP_USUARIOS + "/" + this.state.form.usu_email + "/" + this.state.form.usu_clave).then(response => {
        cookies.set("usu_id", response.data.usu_id, { path: "/"})
        cookies.set("usu_email", response.data.usu_email,{ path: "/"})
        cookies.set("usu_nombres", response.data.usu_nombres,{ path: "/"})
        cookies.set("usu_apellidos", response.data.usu_apellidos,{ path: "/"})
        window.location.href= './'
    }).catch(error => {
        console.log(error)
    })
}

  render() {
    return (
      <div className="containerPrincipal">
      <div className="containerSecundario">
        <div className="form-group">

        <label>Nombres</label>
        <br />
        <input
            type="text"
            className="form-control"
            name="usu_nombres"
            onChange={this.handleChange}
          />
          <br />

        <label>Apellidos</label>
        <br />
        <input
            type="text"
            className="form-control"
            name="usu_apellidos"
            onChange={this.handleChange}
            />
            <br />

            <label>Contraseña: </label>
              <br />
              <input
                type="password"
                className="form-control"
                name="usu_clave"
                onChange={this.handleChange}
              />
              <br />

            <label>Ingrese nuevamente la contraseña: </label>
        <br />
        <input
            type="password"
            className="form-control"
            name="registroClave"
            onChange={this.handleChange}
            />
            <br />

            <button className="btn btn-primary"onClick={()=> this.registrarUsuario()}>Registrarse</button>
      </div>
      </div>
      </div>
    );
  }
}

export default PageRegistro;
