import React, { Component } from "react";
import '../css/Login.css'
import 'bootstrap/dist/css/bootstrap.min.css'
import axios from "axios";
import Cookies from "universal-cookie";

const cookies = new Cookies();

class PageRegistro extends Component {

  state = {
    form: {
      name: '',
      lastname: '',
      usu_email: '',
      pwd: '',
      repwd: ''
    }

  }

  handleChange = async e => {
    await this.setState({
      form: {
        ...this.state.form,
        [e.target.name]: e.target.value
      }
    })
    console.log(this.state.form)
  }

  registrarUsuario = async () => {
    let name = this.state.form.name
    let lastname = this.state.form.lastname
    let repwd = this.state.form.repwd
    let usu_email = this.state.form.usu_email
    let pwd = this.state.form.pwd
    if (name === '' || lastname ==='' || usu_email ==='' || lastname === '') {
      alert('Se requieren todos los datos')
      return "Datos Vacios"
    }

    if (pwd !== repwd) {
      alert('Las contraseñas no coinciden')
      return "Contraseñas no coinciden"
    }

    axios.get(process.env.REACT_APP_USUARIOS+"/"+name+"/"+pwd).then(response => {
      if (response.data.length > 0) {
        alert('El usuario ya existe')
        return "El usuario ya existe"
      }

      delete this.state.form.reClave
      alert('Gracias por registrarse')
      this.peticionPost();
    }).catch(error => {
      console.log(error)
    })

  }

  peticionPost = () => {
    axios.post(process.env.USUARIOS, this.state.form).then(response => {
      //console.log(response.data)
      this.iniciarSesion()
    }).catch(error => {
      console.log(error)
    })
    this.iniciarSesion()
  }

  iniciarSesion = () => {
    axios.get(process.env.USUARIOS + "/" + this.state.form.usu_email + "/" + this.state.form.usu_clave).then(response => {
      cookies.set("usu_id", response.data.usu_id, { path: "/" })
      cookies.set("usu_email", response.data.usu_email, { path: "/" })
      cookies.set("usu_nombres", response.data.usu_nombres, { path: "/" })
      cookies.set("usu_apellidos", response.data.usu_apellidos, { path: "/" })
      window.location.href = './'
    }).catch(error => {
      console.log(error)
    })
  }

  render() {
    return (
      <div className="containerPrincipal">
      <div className="containerSecundario">
        <div className="form-group">

        <label>Nombres</label>
        <input
            type="text"
            className="form-control"
            name="name"
            onChange={this.handleChange}
          />
          <br />

        <label>Apellidos</label>
        <input
            type="text"
            className="form-control"
            name="lastname"
            onChange={this.handleChange}
            />
            <br />

            <label>Email</label>
        <input
            type="text"
            className="form-control"
            name="usu_email"
            onChange={this.handleChange}
            />
            <br />

            <label>Contraseña: </label>
              <input
                type="password"
                className="form-control"
                name="pwd"
                onChange={this.handleChange}
              />
              <br />

            <label>Ingrese nuevamente la contraseña: </label>
        <input
            type="password"
            className="form-control"
            name="repwd"
            onChange={this.handleChange}
            />

            <button className="btn btn-primary"onClick={()=> this.registrarUsuario()}>Registrarse</button>
      </div>
      </div>
      </div>
    );
  }
}

export default PageRegistro;
