import React, { Component } from "react";
import '../css/Login.css'
import 'bootstrap/dist/css/bootstrap.min.css'
import axios from "axios";
import Cookies from "universal-cookie";

const cookies = new Cookies();

class PageLogin extends Component {
  state = {
    form: {
      usu_email: '',
      clave: ''
    }
  }

  handleChange = async e => {
    await this.setState({
      form: {
        ...this.state.form,
        [e.target.name]: e.target.value
      }
    })
    console.log(this.state.form);
  }

  iniciarSesion = async () => {
    let name = this.state.form.usu_email
    let pwd = this.state.form.clave
    if (name === '' || pwd ==='' ){
      alert('Se requiere usuario')
      return 'Datos vacios'
}

    await axios.get(process.env.REACT_APP_USUARIOS + "/" + name + "/" + pwd)
      .then(response => {
        return response.data
      }).then(response => {
        console.log(response)
        if (response) {
          cookies.set("_id", response._id, { path: "/" })
          cookies.set("usu_email", response.usu_email, { path: "/" })
          cookies.set("usu_nombres", response.usu_nombres, { path: "/" })
          cookies.set("usu_apellidos", response.usu_apellidos, { path: "/" })
          cookies.set("per_id", response.per_id, { path: "/" })
          alert("Bienvenid@ " + response.usu_nombres)
          this.setState()
          window.location.href = './PageInicio'
        } else {
        }
      })
      .catch(error => {
        alert("Verificar usuario y/o contraseña")
        console.log(error)
      })
  }

  render() {
    return (
      <div className="containerPrincipal">
        <div className="containerSecundario">
          <div className="form-group">
            <h1>Bienvenido!</h1>
            <h8>Ir a marcadores deportivos</h8>
            <br />
            <br />
            <br />
            <div class="d-flex justify-content-center">
        <form>
          <div class="mb-3">
            <label for="exampleInputEmail1" 
            class="form-label">Correo electrónico</label>
            <input type="email" 
            class="form-control" 
            name="usu_email" 
            id="usu_email"  
            onChange={this.handleChange}
            aria-describedby="emailHelp" />
            <div id="emailHelp" class="form-text"></div>
          </div>
          <div class="mb-3">
            <label for="exampleInputPassword1" 
            class="form-label">Contraseña</label>
            <input type="password" 
            class="form-control" 
            name="clave" 
            id="clave" 
            onChange={this.handleChange}
            />
          </div>
          <button type="submit" class="btn btn-primary"onClick={() => this.iniciarSesion()}>Iniciar sesión</button>
        </form>
      </div>
        </div>
      </div>
      </div>
    );
  }
}

export default PageLogin;
